template.value:
return fetch('/url', {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    key: value,
  })
})
.then((response) => { 
  return response.json();
})
.then((response) => {
  return response;
})
.catch((error) => {
  return error;
});
